// make our requests to our backend
import { GET_ITEMS, ADD_ITEM, DELETE_ITEM, ITEMS_LOADING } from './types';

// each returns an action object with a type (and payload) field(s)
export const getItems = () => {          // all we are doing is sending the type to itemReducer
    return {
        type: GET_ITEMS                  // this is the action
    };
};

export const deleteItem = (id) => {     // takes in id to know which item to delete
    return {
        type: DELETE_ITEM, 
        payload: id                    //   
    };
};

export const addItem = item => {     // takes in id to know which item to delete
    return {
        type: ADD_ITEM, 
        payload: item           
    };
};

export const setItemsLoading = () => {
    return {
        type: ITEMS_LOADING
    }
}
