// make our requests to our backend
import { GET_ITEMS, ADD_ITEM, DELETE_ITEM, ITEMS_LOADING } from './types';
import axios from 'axios';

// each returns an action object with a type (and payload) field(s)
export const getItems = () => dispatch => {         
    dispatch(setItemsLoading());                     // dispatch() to send the type to the reducer -> returns whatever is in the state and sets loading to true     
    axios.get('/api/items')                          // get request to 'api/items' which is our backend
      .then(res => dispatch({
          type: GET_ITEMS,
          payload: res.data
      })
      )
};

export const deleteItem = (id) => dispatch => {     // takes in id to know which item to delete
  axios.delete(`/api/items/${id}`).then(res =>
    dispatch({
      type: DELETE_ITEM,
      payload: id
    })
)
};

export const addItem = item => dispatch => {     // takes in id to know which item to delete
  axios.post('/api/items', item)
    .then(res => 
      dispatch({
        type: ADD_ITEM,
        payload: res.data
      })
    )
};

export const setItemsLoading = () => {
    return {
        type: ITEMS_LOADING
    }
}
