// React component to Add items to the store
import React, { Component } from 'react';
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  FormGroup,
  Label,
  Input
} from 'reactstrap';

//Container = component that's hooked to redux
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { addItem } from '../actions/itemActions';
import uuid from 'react-uuid';

class ItemModal extends Component {
  state = {
    modal: false,                  // represents if the modal is open or not
    name: ''
  }

  static propTypes = {
    isAuthenticated: PropTypes.bool
  }

  toggle = () => {
    this.setState({
      modal: !this.state.modal
    });
  }
  onChange = (e) => {                           // whatever was added to add item on UI
    this.setState({ [e.target.name]: e.target.value }); // e.target.name get's name from line 68, which comes from input to UI
  }

  onSubmit = e => {                         // get input from UI button
    e.preventDefault();                    // prevent form actually submitting

    const newItem = {                     // create new item 

      name: this.state.name,            //whatever is typed into the input is update into state.name
      userID: this.state.userID
    }

    //Add item via addItem action
    this.props.addItem(newItem);

    //close modal
    this.toggle();
  }

  render() {
    return (
      <div>
        {this.props.isAuthenticated ? <Button
          color="dark"
          style={{ marginBottom: '2rem' }}
          onClick={this.toggle}
        >AddItem</Button> : <h4 className="mb-3 ml-4">Please log in to manage items</h4>}

        <Modal
          isOpen={this.state.modal}
          toggle={this.toggle}
        >
          <ModalHeader toggle={this.toggle}>Add to Shopping List</ModalHeader>
          <ModalBody>
            <Form onSubmit={this.onSubmit}>
              <FormGroup>
                <Label for="item">Item</Label>
                <Input
                  type="text"
                  name="name"
                  id="item"
                  placeholder="Add shopping item"
                  onChange={this.onChange}
                >
                </Input>
                <Button
                  color="dark"
                  style={{ marginTop: '2rem' }}
                  block
                >
                  Add Item
                        </Button>
              </FormGroup>
            </Form>
          </ModalBody>
        </Modal>
      </div>
    );
  }
}
// Here we can bring in any reducer from index.js
const mapStateToProps = state => ({
  item: state.item,
  isAuthenticated: state.auth.isAuthenticated,
  userID: state.auth.userID
});

export default connect(mapStateToProps, { addItem })(ItemModal);
