import React, { Component } from 'react';
import {
  Container,
  ListGroup,
  ListGroupItem,
  Button
} from 'reactstrap';
import {
  CSSTransition, 
  TransitionGroup
} from 'react-transition-group';
import uuid from 'react-uuid';             //generate ids for items = static for now
import { connect } from 'react-redux';           // get state from redux into a react component
import { getItems } from '../actions/itemActions';
import PropTypes from 'prop-types';

class ShoppingList extends Component {
                           // an array of items, hardcoded for now will later be from the mongoDB
  componentDidMount() {    // runs when component mounts such as making an API request
    this.props.getItems();
  }

  render() {
    const { items } = this.props.item.items;   // {} = destructoring, we pull items from this.state and put into variable items
    console.log(items);
    return(
        <Container>
            <Button 
            color="dark" 
            style={{marginBottom: '2rem'}} 
            onClick={() => {
                const name = prompt('Enter Item');
                if(name) {
                    this.setState(state => ({
                        items: [...state.items, {id: uuid(), name}]
                    }));
                }
            } }>
              Add Item 
            </Button>
            <ListGroup>
                <TransitionGroup className="shopping-list">
                    {items.map(({id, name}) => (
                      <CSSTransition key={id} timeout={500} classNames="fade">
                          <ListGroupItem>
                            <Button
                              className="remove-btn"
                              color="dark"
                              size="sm"
                              onClick={() => {
                                this.setState(state => ({
                                    items: state.items.filter(item => item.id != id)
                                }));
                            }}
                            >
                              &times;
                            </Button>
                              {name}
                          </ListGroupItem>
                      </CSSTransition>  
                    ))}
                </TransitionGroup>
            </ListGroup>
        </Container>
    );
  }
}

ShoppingList.propTypes = {
    getItems: PropTypes.func.isRequired,      // set the type to a function that is required
    item: PropTypes.object.isRequired                   // object that represents our state
};

const mapStateToProps = state => ({
    item: state.item                        // based on reducer in index.js
});

// since we are using connect to get state from redux we need to export using connect
export default connect(mapStateToProps, { getItems } )(ShoppingList);
//                   function, any actions we want to use, the component
