import React, { Component } from 'react';
import {
  Container,
  ListGroup,
  ListGroupItem,
  Button
} from 'reactstrap';
import {
  CSSTransition, 
  TransitionGroup
} from 'react-transition-group';
import uuid from 'uuid';             //generate ids for items = static for now

class ShoppingList extends Component {
  state = {                          // an array of items, hardcoded for now will later be from the mongoDB
    items: [
        {id: uuid(), name: 'Eggs'},
        {id: uuid(), name: 'Milk'},
        {id: uuid(), name: 'Steak'},
        {id: uuid(), name: 'Water'}
    ]
  }

  render() {
    const { items } = this.state;   // {} = destructoring, we pull items from this.state and put into variable items
    return(
        <Container>
            <Button 
            color="dark" 
            style={{marginBottom: '2rem'}} 
            onClick={() => {
                const name = prompt('Enter Item');
                if(name) {
                    this.setState(state => ({
                        items: [...state.items, {id: uuid(), name}]
                    }));
                }
            } }>
              Add Item 
            </Button>
        </Container>
    )
  }
}

export default ShoppingList;
